import React,{useState,useEffect} from 'react';
import {useParams} from 'react-router-dom'
import '../styling/Chat.css';
import {Avatar,IconButton } from '@material-ui/core';
import DonutLargeIcon from '@material-ui/icons/DonutLarge';
import ChatIcon from '@material-ui/icons/Chat';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import {SearchOutlined} from '@material-ui/icons'
import SidebarChat from './SidebarChat'
import InsertEmoticonIcon from '@material-ui/icons/InsertEmoticon';
import MicIcon from '@material-ui/icons/Mic';
import axios from'axios'



function Chat({user}) {
    const [input, setInput] = useState("");
    const [seed, setseed] = useState("")
    const [chat, setchat] = useState({})
    const [roomChats, setRoomChats] = useState({})
    const {roomId} = useParams();
    useEffect(() => {
        setseed(Math.floor(Math.random()*5000));
        axios.post("https://loclhost//8080/getRoom",roomId)
                .then(response=>{
                    alert(response.data);
                    setRoomChats({...roomChats,room : response.data})
                })
                .catch(error=>{
                    console.log(error);
                }
                )

    }, [chat]) 

    const sendchat=(e)=>{
        e.preventDefault();
        // alert(input);
        // console.log(input);
        
        setchat({...chat,"message":input,"userId":user,"roomId":roomId})
        axios.post("https://loclhost//8080/addChat",chat)
            .then(response=>{
                alert(response.data);
            })
            .catch(error=>{
                console.log(error);
                }
            )

        setInput('');
    }
    
    return (
        <div className='chat'>
           <div className='chat_header'>
                <Avatar src={`https://avatars.dicebear.com/api/human/${seed}.svg`} />
                <div className='chat_header_info'>
                    <h3>{roomChats.room.roomName}</h3>
                    <p>Last seem at ...</p>
                </div>
                <div className='chat_headerRight'>
                    <IconButton>
                        <SearchOutlined/>
                    </IconButton>
                    <IconButton>
                        <ChatIcon/>
                    </IconButton>
                    <IconButton>
                        <MoreVertIcon/>
                    </IconButton>
                </div>

           </div>

           {roomChats.room.map((i)=>{
           <div className='chat_body'>
                <p className={`chat_chat ${true && 'chat_reciever'}`}>
                    <spam className='chat_name'>
                        {roomChats.room[i].userId}
                    </spam>
                    {roomChats.room[i].message}
                    <spam className='chat_timestamp'>
                        {roomChats.room[i].timeStamp}
                    </spam>
                </p>
            </div>}
            )
           }     
           
           <div className='chat_footer'>
                
                <InsertEmoticonIcon></InsertEmoticonIcon>
                <form>
                    <input value={input} placeholder="type chat ..." type="text" onChange={e=>setInput(e.target.value)}/>
                    <button onClick={sendchat} type='submit'>Send</button>

                </form>
                <MicIcon/>
           </div>


        </div>
    )
}

export default Chat
