import React,{useState,useEffect} from 'react'
import "../styling/SidebarChat.css";
import {Avatar,IconButton} from '@material-ui/core'
import {Link} from "react-router-dom";
import axios from'axios'

function SidebarChat({addNewChat ,roomId}) {
    const [seed, setseed] = useState("")

    useEffect(() => {
        setseed(Math.floor(Math.random()*5000));
       
    }, [])

    const createChat=()=>{
        const roomName = prompt("Enter name")
        const dis = prompt("Enter discription")
        if(roomName){
            const temp = {roomName:{roomName},discription:{dis}}
            axios.post("https://loclhost//8080/addRoom",temp)
                .then(response=>{
                    alert(response.data);

                })
                .catch(error=>{
                    console.log(error);
                }
                )
        }
    }

    const showChat=(name)=>{
        alert("hi");
        // show chats with roomName
    }

    return !addNewChat ? (
        
        <Link to = {`/romm/${roomId}`}>
        <div onClick={(e)=>showChat()} className='sidebarChat' >
            <Avatar src={`https://avatars.dicebear.com/api/human/${seed}.svg`} />
            <div className="sidebarChat_info">
                <h2>{roomId}</h2>
                {/* <h3>{discrip}</h3> */}
                <p>message..</p>
            </div>

        </div>
        </Link>)
        :(
            <div onClick={createChat} className='sidebarChat'>
                <h2>Add New Chat</h2>
            </div>
        );
}

export default SidebarChat
